void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define sp 63.0/512.0

vec2 n(vec2 p){return p/iResolution.xy;}

float minusOnePow(int n){
    if(mod(float(n),2.)==0.) return 1.0;
    return -1.0;
}

vec4 rule(vec2 p){
    vec2 k1 = vec2(2, 1), k2 = vec2(2, -1);
    vec4 one = texture(iChannel0, n(p + k1));
    vec4 two = texture(iChannel0, n(p - k1));
    vec4 thr = texture(iChannel0, n(p + k1.yx));
    vec4 fur = texture(iChannel0, n(p - k1.yx));
    vec4 fiv = texture(iChannel0, n(p + k2));
    vec4 six = texture(iChannel0, n(p - k2));
    vec4 sev = texture(iChannel0, n(p + k2.yx));
    vec4 ate = texture(iChannel0, n(p - k2.yx));
    return smoothstep(vec4(0), vec4(1), one - two + thr - fur + fiv - six + sev - ate);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame < 10) fragColor = vec4(0);
    ivec2 sqr = ivec2(mod(vec2(fragCoord),vec2(256., 128.)));
    if(iMouse.xy == vec2(0) && iFrame < 5 && sqr.x < 5 && sqr.y < 5)
       fragColor = texture(iChannel1, n(fragCoord));
    else fragColor = rule(fragCoord);
    if(iMouse.xy != vec2(0) && distance(iMouse.zw, fragCoord) < 10.0)
        fragColor = texture(iChannel1, n(fragCoord));
}
