void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 start_point = vec2(0.0, 0.0);
    vec2 center_point = iResolution.xy / 2.0;
    float circle_radius = center_point.y * 0.8 ; 

    float greenColor = step(distance(center_point, fragCoord), circle_radius);
    float blueColor = max(step(center_point.x, fragCoord.x) - greenColor, 0.0);
    float redColor = 1.0 - blueColor - greenColor;

    fragColor = vec4(redColor, greenColor, blueColor,1.0);
}
