// by Andrey A. Ugolnik / Tiny Orange, OOO
// 2015

// grid size
const float size = 20.0;
// colors
const vec4 col0 = vec4(0.5, 0.5, 0.5, 1.0);
const vec4 col1 = vec4(0.7, 0.7, 0.7, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //float delta = 0.0;
    float delta = iTime * 10.0;

    bool a = int(mod(fragCoord.x + delta, size)) == 0;
    bool b = int(mod(fragCoord.y + delta, size)) == 0;

	fragColor = ((a && !b) || (!a && b)) ? col0 : col1;
}
