void mainImage( out vec4 f, in vec2 g )
{
    f.xyz = iResolution;

    float 
        z = 25.,
        r = f.x/f.y*z,
        p, c;

    g = z * (g+g-f.xy)/f.y;

    p = length(g);

    c = .3 / (g.y + z) - .3 / (g.y - z) + .3 / (g.x + r) - .3 / (g.x - r);

    g += p * cos( p - vec2(9.4,8.6) * iDate.w ); 

    c += 1. / dot(g, g); 

    c = smoothstep(c -2., c +1.2, 1.);

   	f = vec4(c, -2./c + c*3., -4./c + c*5., 1);

}
