mat2 rotate(float a)
{
	return mat2(cos(a), sin(a), -sin(a), cos(a));	
}

float deTorusKnot(in vec3 p)
{     
    float a = atan(p.z, p.x);
    p.xz *= rotate(a);
    p.x -= 1.0;
    p.xy *= rotate(a * 2.5);
    p.x = abs(p.x) - 0.3;
	return length(p.xy) - 0.2;
}

float deMobius(in vec3 p)
{     
  	float a = atan(p.z, p.x);
    p.xz *= rotate(a);
    p.x -= 1.0;
	p.xy *= rotate(a * 0.5);
    return abs(p.x) + abs(p.y) - 0.2;
}

float map(in vec3 p)
{
     return mix(deTorusKnot(p), deMobius(p), step(5.0, mod(iTime, 8.0)));
}

vec3 calcNormal(in vec3 p)
{
	const vec2 e = vec2(0.0001, 0.0);
	return normalize(vec3(
		map(p + e.xyy) - map(p - e.xyy),
		map(p + e.yxy) - map(p - e.yxy),
		map(p + e.yyx) - map(p - e.yyx)));
}

float march(in vec3 ro, in vec3 rd)
{
	const float maxd = 5.0;
	const float precis = 0.001;
    float h = precis * 2.0;
    float t = 0.0;
	float res = -1.0;
    for(int i = 0; i < 100; i++)
    {
        if(h < precis || t > maxd) break;
	    h =min( map(ro + rd * t),0.1); // rewrite 2018/01/10
        t += h;
    }
    if(t < maxd) res = t;
    return res;
}

vec3 transform(in vec3 p)
{
    p.y -= 0.06;
    p.yz *= rotate(0.9);
    p.zx *= rotate(iTime * 0.6);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
	vec3 col = vec3(0.1 + p.y * 0.15);
    vec3 rd = normalize(vec3(p, -1.8));
	vec3 ro = vec3(0.0, 0.0, 3.0);
    vec3 li = normalize(vec3(0.5, 0.8, 3.0));
    ro = transform(ro);
	rd = transform(rd);
	li = transform(li);
    float t = march(ro, rd);
    if(t > -0.001)
    {
        vec3 pos = ro + t * rd;
        vec3 n = calcNormal(pos);
		float dif = clamp((dot(n, li) + 0.5) * 0.7, 0.3, 1.0);
        col = vec3(0.85, 0.85, 0.9) * dif;
	}
   	fragColor = vec4(col, 1.0);
}
