void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime;
    float x = fragCoord.x, y = fragCoord.y;
    uv.y += sin(t * 2.0 + y / 16.0) * 0.05;
    uv.x += sin(t * 3.0 + x / 16.0) * 0.05;
    vec4 p = texture(iChannel0, uv);
    fragColor = p;
}
