//noise function copied from inigo quilez music - Pirates shader   https://www.shadertoy.com/view/ldXXDj
float fbm( vec2 p )
{
    return 0.5000*texture( iChannel1, p*1.00 ).x + 
           0.2500*texture( iChannel1, p*2.02 ).x + 
           0.1250*texture( iChannel1, p*4.03 ).x + 
           0.0625*texture( iChannel1, p*8.04 ).x;
}


//function borrowed from inigo quilez article https://iquilezles.org/articles/palettes
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float r, g, b, i, a;
    vec2 h = vec2(0.5, iMouse.y/iResolution.y);
    h = iMouse.xy/iResolution.xy;
    vec2 h2;
    h2 = h-uv;
    a = h2.y/h2.x;
    a = atan(abs(a));

    float nmod = 3.0 - pow(mod(iTime*4.0, 81.0), 0.25);
    vec3 col = pal( a+fbm(uv*vec2(nmod)+vec2(pow((iTime)*5.0, 0.25))), vec3(1.0,0.6,0.0),vec3(0.1,0.3,0.5),vec3(2.0,3.0,0.5),vec3(0.5,0.0,0.7));
    fragColor = vec4(col.xyzx); 
    float d = length(uv - h);
    float g2 =  1.0 - smoothstep( 0.1, 0.4, d );
    fragColor = mix(fragColor, vec4(1.0, 1.0, 0.0, 1.0), g2);


}
