//void mainImage( out vec4 c, vec2 f )
//to be careful,don"t use the 'out',because some browser report error!!!!
void mainImage( out vec4 c, vec2 f )
{
    c -= c;
    f = .5-f/iResolution.xy;
    c.r = smoothstep(.1,1.,.8-length(f+vec2(f.x/2.,abs(f.x))));
    c += pow(c.r,1.+.2*fract(iDate.w));
}

