#define sqrtOf2Halfed 0.70710678118

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenMiddle = vec2(.5,.5);
	vec2 uv = (fragCoord.xy / iResolution.xy) - screenMiddle;
 	float distanceToMiddleNomalized= length(uv)/pow(sqrtOf2Halfed,.1);

    float increase =  distanceToMiddleNomalized * abs(fract(iTime*0.5) * 2.0 - 1.0) ;

    fragColor.g = texture(iChannel0,uv+ screenMiddle).g;
    fragColor.b = texture(iChannel0,uv *(1.0 -increase *.1)+ screenMiddle).b;
    fragColor.r = texture(iChannel0,uv *(1.0 -increase *.2)+ screenMiddle).r;


}
