vec2 Position = vec2(0.5,0.5);
vec2 maxSquare = vec2(0.75,0.75);
vec2 minSquare = vec2(0.25,0.25);
float Radius = .25;
float Radius2 = .1;
float Radius3 = .15;
vec4 FGColor = vec4(1.0,1.0,1.0,1.0);
vec4 BGColor = vec4(0.0,0.0,0.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord.xy / iResolution.xy;
vec4 OtherColor = vec4 (0.0 + 0.5 *cos(iTime),0.0 + 0.5 *sin(iTime),1.0 + 0.5 *cos(iTime),1.0);

//Question 1
if(uv.x > minSquare.x && uv.x < maxSquare.x && uv.y > minSquare.y && uv.y < maxSquare.y)
fragColor = FGColor;
else
fragColor = BGColor;


//Question 2 and Question 3
float aspectRatio = iResolution.y / iResolution.x;
Position.y *= aspectRatio;
uv.y *= aspectRatio;



if(length(Position - uv) <= Radius2)
{
fragColor = BGColor;
}
else if(length(Position - uv) <= Radius3)
{
fragColor = OtherColor;
}
else if(length(Position - uv) <= Radius)
{
fragColor = FGColor;
}
else
fragColor = BGColor;

}
