float hash(float n)
{
 	return fract(n*n*iTime*.02);   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
   	uv = ceil(uv*40.+40.*vec2(sin(iTime/13.),cos(iTime/2.)));


    fragColor = vec4(hash(2.0+iTime*uv.x), hash(3.0+iTime*uv.x), hash(4.0+iTime*uv.x), 1.0);
}
