void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;

    //Making the flow looks concave
    vec4 col = vec4(uv,0.5+0.5*sin(iTime),1.0);

    //Scale up UV
    uv *= 0.2;
    uv += col.xy*uv;

    //Make time slower
    float t = iTime*0.15;

    //2 layers of speed, with some distortion
    vec4 c = texture(iChannel0,uv-t+col.xy*uv);
    vec4 d = texture(iChannel0,(vec2(1)-uv)+t*1.5-col.xy*uv);

    c-=0.3;
    d-=0.3;

    vec4 f = texture(iChannel0,uv/10.0);

    float dist1 = length(c-f);
    float dist2 = length(d-f);


    vec4 result = vec4(min(dist1,dist2));

    vec4 g = texture(iChannel1,uv/vec2(dist1,dist2));

    result = mix(result,g,result.r);



	fragColor = result;

}
