const float smooth_area = 0.1; // how wide is smooth area

const bool decrease = false; // false = black increases, true = black decreases

const bool invert_progression = false; // true = progression goes left, false = progression goes right

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//vec2 corrected_coord = vec2(fragCoord.x, iResolution.y - fragCoord.y);
    //float biggest_dim = max(iResolution.x, iResolution.y);
    //vec2 st = corrected_coord/biggest_dim;
    vec2 st = fragCoord.xy/iResolution.xy;
    // PARAMETERS

    //float time = 0.9999;

    // CODE
    float t = fract(iTime);
    t = invert_progression?1.-t:t;
    t = (1.0 + smooth_area*2.) * t - smooth_area;
    //f_st -= 0.5;
    float a = (smoothstep(t-smooth_area, t+smooth_area, st.x));
    a = decrease?a:1.-a;
    //result_color.a = 1-(step(t, abs(f_st.x+f_st.y))+step(t, abs((1-f_st.x)-(1-f_st.y))));
    fragColor = texture(iChannel0, st) * (1.-a);
}
