vec3 sdSphere( in vec3 d, in vec3 c, in vec3 q, in float a, in float w, vec4 road, vec4 grass, vec4 sky )
{    
	if (d.y > .04) {
		return sky.rgb;//c; //sky blue
	} else if (sin(4. * q.z + 40. * a) > 0.) {
		if (w > 2.) {
	  		return c.xyx; //green
		} else if (w > 1.2) {
			return d.zzz; //white
	  	} else return c.yyy; //grey
		} else if (w > 2.) {
			return grass.rgb;//c.xzx;//green grass
  			} else if (w > 1.2) {
	  			return c.yxx * 2.; //red
				} else if (w > .004) {
					return road.xxx;//c.zzz; //grey road
	  				} else return d.zzz; //white
}

void mainImage( out vec4 f, in vec2 p ){

    vec2 uv = p/iResolution.xy;
    vec4 road = texture( iChannel0, uv );    
    vec4 grass = texture( iChannel1, uv );   
    vec4 sky = texture( iChannel2, uv );

    vec3 q=iResolution,d=vec3(p.xy-.5*q.xy,q.y)/q.y,c=vec3(0,.5,.7);

    q=d/(.1-d.y);
    float a=iDate.w, k=sin(.2*a), w = q.x *= q.x-=.05*k*k*k*q.z*q.z;


    f.xyz= sdSphere(d,c,q,a,w,road,grass,sky);

}
