void mainImage(out vec4 outCol, vec2 coord) {
    vec2 uv = coord / iResolution.xy;
    float factor = sin(iTime * 5.0 * uv.x) * 0.5 + 0.5;

    vec3 bg_col = mix(vec3(0.0), vec3(1.0, 0.0, 0.5), uv.y);
    vec3 fg_col = mix(vec3(0.0, 1.0, 0.5), vec3(1.0), uv.y);

    vec3 col = uv.y > factor ? bg_col : fg_col;

    outCol = vec4(col, 1.0);
}
