uniform sampler2D tex;

float val = 4.0;

vec3 toyuv(vec3 color) {
	return vec3(color.r * 0.299 + color.g * 0.587 + color.b * 0.114,
				color.r * -0.169 + color.g * -0.331 + color.b * 0.5 + 0.5,
                color.r * 0.5 + color.g * -0.419 + color.b * -0.081 + 0.5);
}

vec3 torgb(vec3 i) {
	vec3 yuva = vec3(i.x, (i.y - 0.5), (i.z - 0.5));
	return vec3(yuva.x * 1.0 + yuva.y * 0.0 + yuva.z * 1.4,
				yuva.x * 1.0 + yuva.y * -0.343 + yuva.z * -0.711,
				yuva.x * 1.0 + yuva.y * 1.765 + yuva.z * 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = texture(tex, uv).rgb;
    vec3 yuvC = toyuv(color);
    yuvC.x = float(int(yuvC.x * val + fract(sin(iTime))))/val - (fract(sin(iTime)) / val);

    vec3 newColor = torgb(yuvC);
    fragColor = vec4(newColor, 1);
}
