//noise and rand2 function from thebookofshaders.com
float rand2 (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = rand2(i);
    float b = rand2(i + vec2(1.0, 0.0));
    float c = rand2(i + vec2(0.0, 1.0));
    float d = rand2(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    vec2 u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

float fbm(vec2 p){
	float f = 0.0;
    f+=0.5*noise(p); p*=2.03;
    f+=0.25*noise(p); p*=2.03;
    f+=0.125*noise(p); p*=2.03;
    f+=0.0625*noise(p); p*=2.03;
    f+=0.03125*noise(p); p*=2.03;
    f+=0.015625*noise(p); p*=2.03;
    f/=0.984325;
    return f;
}

mat2 rotate(float p){
	return mat2(
    	cos(p),sin(p),
        -sin(p),cos(p)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv *2.0 -1.0;

    uv.x*= iResolution.x/iResolution.y;

    vec2 p = uv;

    mat2 m=rotate(fbm(p+iTime));

    p*=m;

    float n=fbm(p*3.0);

    n=abs(n);

    //n = pow(n,2.0);

    vec3 fc = vec3(n);

    fc*=vec3(0.1,0.6,0.8);

    //vignette

    float f = length(uv);


    float c=smoothstep(2.5,0.8,f);

    fc*=c;




	fragColor = vec4(fc,1.0);
}
