float displacement(float time){
    time = max(time, 0.0);
    float d = 0.0;
    for(float i = 3.0; i < 7.0; i++){
    	d += (10.0 / (i)) * (sin(i * 110.0 * time));
    }
    for(float i = 0.00; i < 60.0; i+=5.0){
    	d += i * sin(1000.0 * time)/(((time-i)*(time-i) + 1.0));
    }
    d *= min(log(time + 1.0), 1.0);
    return d;
}    

