// Animated Worley Noise - by Jakob Thomsen
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

vec2 refl2(vec2 v)
{
    return abs(2.0 * fract(v * 0.5) - 1.0);
}

float worleydist(int seed, vec2 pos) // NOTE: seed must be in [0..127]
{
    //return length(pos - vec2(float(perm[2 * seed + 0]) / 255.0, float(perm[2 * seed + 1]) / 255.0));
    return length(pos - refl2(0.1 * iTime * texture(iChannel0, vec2(float(seed) / 20.0, 0.0)).xy));
}

float worleynoise(vec2 pos)
{
    float d = worleydist(0, pos);
//    for(int i = 0; i < 128; i++)
//    {
//        d = min(d, worleydist(i, pos));
//    }
    d = min(d, worleydist(1, pos));
    d = min(d, worleydist(2, pos));
    d = min(d, worleydist(3, pos));
    d = min(d, worleydist(4, pos));
    d = min(d, worleydist(5, pos));
    d = min(d, worleydist(6, pos));
    d = min(d, worleydist(7, pos));
    d = min(d, worleydist(8, pos));
    d = min(d, worleydist(9, pos));
    d = min(d, worleydist(10, pos));
    d = min(d, worleydist(11, pos));
    d = min(d, worleydist(12, pos));
    d = min(d, worleydist(13, pos));
    d = min(d, worleydist(14, pos));
    d = min(d, worleydist(15, pos));
    d = min(d, worleydist(16, pos));
    d = min(d, worleydist(17, pos));
    d = min(d, worleydist(18, pos));
    d = min(d, worleydist(19, pos));
    d = min(d, worleydist(20, pos));
    d = min(d, worleydist(21, pos));
    d = min(d, worleydist(22, pos));
    d = min(d, worleydist(23, pos));
    d = min(d, worleydist(24, pos));
    d = min(d, worleydist(25, pos));
    d = min(d, worleydist(26, pos));
    d = min(d, worleydist(27, pos));
    d = min(d, worleydist(28, pos));
    d = min(d, worleydist(29, pos));
    d = min(d, worleydist(30, pos));
    d = min(d, worleydist(31, pos));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = worleynoise(fragCoord.xy / iResolution.xy);
    float b = pow(1.0 - worleynoise(fragCoord.xy / iResolution.xy), 20.0);
    fragColor = vec4(vec3(max(a * 5.0, b)), 1.0);
}
