// Sine Tree - written 2015-11-05 by Jakob Thomsen
// A tree-like structure without recursion, using trigonometric functions with fract for branching.
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define pi 3.1415926
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;
    float n = 5.0; // fract(t / 6.0) * 6.0;
    float br = pow(2.0, ceil(uv.y * n));
    float fr = fract(uv.y * n);
    float v = cos(pi * (uv.x * br + pow(fr, 1.0 - fr) * 0.5 * sign(sin(pi * uv.x * br))));
    float val = pow(0.5 - 0.5 * v, (fr * 1.5 + 0.5) * 100.0);
    //float val = smoothstep(0.00, 0.01, abs(fract(uv.x / 2.0) * pow(2.0, ceil(uv.y * n)) - 0.5));
	fragColor = vec4(vec3(1.0 - uv.y, uv.y, 0.0) * val, 1.0);
}
