// Julia Ensemble
// no use of GLSL vector tricks here, because there is a teaching purpose

void mainImage (out vec4 color, in vec2 pixCoords) {


    //************ CAMERA

    float zoom = iResolution.x / 5.0;
    float x = (pixCoords.x - 0.5 * iResolution.x) / zoom;
    float y = (pixCoords.y - 0.5 * iResolution.y) / zoom;


    float cX = (iMouse.x - 0.5 * iResolution.x) / zoom;
    float cY = (iMouse.y - 0.5 * iResolution.y) / zoom;



    //************ GRID

    float lineSemiWidth = 0.01;

    if (abs(x) < lineSemiWidth || abs(y) < lineSemiWidth) {
		color = vec4(1.0, 0.5, 0.5, 1.0);
        return;
    }


    if (mod(x, 1.0) < lineSemiWidth || mod(y, 1.0) < lineSemiWidth) {
		color = vec4(0.7, 0.7, 0.7, 1.0);
        return;
    }




    //************ JULIA ENSEMBLE


    cX = 0.2;   // play with this value, or comment this line to use mouse
    cY = 0.56;  // play with this value

    const float iMax = 150.0;

    for (float i = 0.0; i < iMax; i++) {
        float tempX = x * x - y * y + cX;
        y = 2.0 * x * y + cY;

        x = tempX;

        if((x * x + y * y) > 4.0) { // if distance to center > 2.0
        	color = vec4(vec3((iMax - i) / iMax), 1.0); //simple lighting depending on i
            return;
    	}
    }


    color = vec4(0.0);

}
