void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    // Distance to nearest point in a grid of
    // (frequency x frequency) points over the unit square
    float frequency = 20.0;
    vec2 st2 = sin(mat2(0.707, -0.707, 0.707, 0.707) * uv * (iDate.w/20.));
    vec2 nearest = 2.0*fract(frequency * st2) - 1.0;
    float dist = length(nearest);
    float radius = 0.25;
	vec3 black = vec3(abs(cos(iDate.w)), abs(sin(iDate.w)), abs(tan(iDate.w)));
    vec3 white = vec3(1.0,1.0,1.0);
    vec3 fragcolor_vec3 = mix(black, white,step(radius, dist));
    fragColor = vec4(fragcolor_vec3, 1.0);

//	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
