void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float screenMin = iResolution.x < iResolution.y ? iResolution.x : iResolution.y;
    float screenMax = iResolution.x > iResolution.y ? iResolution.x : iResolution.y;
	vec2 position = fragCoord.xy / screenMin * 2.0 - vec2(screenMax / screenMin, 1.0);

    vec2 mousePosition;
    mousePosition = iMouse.z > 0.0 || iMouse.w > 0.0 ? iMouse.xy : vec2(iResolution.x * 0.5,  iResolution.y * 0.5);
    mousePosition = mousePosition / screenMin * 2.0 - vec2(screenMax / screenMin, 1.0);

    float mouseScale = 1.3;
    float mouseDistance = length(position - mousePosition) * mouseScale;

	fragColor = vec4(mouseDistance, mouseDistance, mouseDistance, 1.0);
}
