float Circle (vec2 uv, vec2 p, float r, float blur) {

    float d = length(uv - p);
    float c = smoothstep(r, r - blur, d);

    return c;
}

float Band (float t, float start, float end, float blur){
	float step1 = smoothstep (start - blur, start + blur, t);
    float step2 = smoothstep (end + blur, end - blur, t);

   	return step1 * step2;
}

float Rect (vec2 uv, float left, float right, float bottom, float top, float blur) {
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);

    return band1 * band2;
}

float Smiley (vec2 uv, vec2 p, float size) {
    uv -= p;	// translating coordinate system
    uv /= size; // scaling coordinate system

    float mask = Circle(uv, vec2(0.), .4, .01);

    mask -= Circle(uv, vec2(-.08, .08), .05, .01);
    mask -= Circle(uv, vec2(.08, .08), .05, .01);

    float mouth = Circle(uv, vec2(0.), .3, .01);
    mouth -= Circle(uv, vec2(0., 0.1), .33, .01);

    mask -= mouth;

    return mask;
}

float remap01 (float a, float b, float t) {
    return (t-a) / (b-a);
}

float remap (float a, float b, float c, float d, float t) {
    return remap01 (a, b, t) * (d-c) + c;
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

	float mask = 0.;

    float x = uv.x;

    //float m = (x-.5)*(x+.5);
    float m = sin(t + x * 8.) * .1;
    float y = uv.y - m;

    float blur = remap(-.5, .5, .01, .25, x);
    blur = pow(blur * 4., 3.);

    mask = Rect(vec2(x, y), -.6, .6, -.1, .1, blur);

    col = vec3(0.6, 0.9, 0.7) * mask;

	fragColor = vec4(col, 1.0);
}
