vec4 multiply(vec4 original, vec4 ratio);
vec4 multiply(vec4 original, float ratio);
float customRound(float number, float degree);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float SUB_PIXEL_WIDTH = 2.;
    float PIXEL_WIDTH = SUB_PIXEL_WIDTH * 3.;
    vec4 red = vec4(1.2, 0.9, 0.9, 0.);
    vec4 green = vec4(0.9, 1.2, 0.9, 0.);
    vec4 blue = vec4(0.9, 0.9, 1.2, 0.);
    vec4 original = texture(iChannel0, fragCoord / iResolution.xy);


    // Color aligned
    vec4 colorAligned = original;

    colorAligned.x = customRound(colorAligned.x, 4.);
    colorAligned.y = customRound(colorAligned.y, 4.);
    colorAligned.z = customRound(colorAligned.z, 4.);

    // Create a mask
    vec4 rgbMasked = colorAligned;
    if (mod(fragCoord.x, PIXEL_WIDTH) < SUB_PIXEL_WIDTH) {
        rgbMasked = multiply(rgbMasked, red);
    }
    if (mod(fragCoord.x + SUB_PIXEL_WIDTH, PIXEL_WIDTH) < SUB_PIXEL_WIDTH) {
        rgbMasked = multiply(rgbMasked, green);
    }
    if (mod(fragCoord.x + SUB_PIXEL_WIDTH * 2., PIXEL_WIDTH) < SUB_PIXEL_WIDTH) {
        rgbMasked = multiply(rgbMasked, blue);
    }

    if (mod(fragCoord.x, 2.) == 0. || mod(fragCoord.y, 2.) == 0.) {
        rgbMasked.x *= 0.5;
    }

    vec4 noise = rgbMasked;
    float location = iResolution.y * (1. - mod(iTime, 1.));
    float dim = 0.1;
    if (fragCoord.y > location - 20. && fragCoord.y < location + 20.) {
        noise.x -= dim * (1. + 1. / abs(fragCoord.y - location));
        noise.y -= dim * (1. + 1. / abs(fragCoord.y - location));
        noise.z -= dim * (1. + 1. / abs(fragCoord.y - location));
    }

    // Result
	fragColor = noise;
}

vec4 multiply( vec4 original, vec4 ratio ) {
    original.x *= ratio.x;
    original.y *= ratio.y;
    original.z *= ratio.z;

    return original;
}

vec4 multiply( vec4 original, float ratio ) {
    original.x *= ratio;
    original.y *= ratio;
    original.z *= ratio;

    return original;
}

float customRound(float number, float degree) {
    float ratio = 1. / degree;

    for (float i = 1.; i <= degree; i++) {
        if (number < ratio*i) {
            return ratio * (i - 1.);
    	}
    }
}
