float circle( vec2 uv, vec2 center, float radius )
{
    float d = distance( center, uv );
    d /= radius;
    //return mod( d, 0.5 );
    //return step( d, 0.5);
    return smoothstep( 0.5, 0.49, d );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // [-1,1], (0,0) = center, stretch x-coords to make pixels square
	vec2 uv = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec2 center;
    center.x = -cos( iTime ) * 1.26;
    center.y = sin( iTime ) * 0.5;

    float d = circle( uv, center, 1.0 );

    vec4 t1 = texture( iChannel0, uv - center );
    vec4 tex_color = vec4( t1.x, t1.x, t1.x, 1.0 );
	fragColor = vec4( d * 0.55, d * 0.34, d * 0.24, 1.0 ) * tex_color;
}
