float epsilon = 0.560;

bool f_equal(float a, float b){
    if(abs(a-b) < epsilon){
        return true;
    }else{
        return false;
    }
}

bool vec4Equal(vec4 a, vec4 b){
    if(f_equal(a.r, b.r) && f_equal(a.g, b.g) && f_equal(a.b, b.b)){
        return true;
    }else{
     	return false;   
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy;
	vec4 britTexColor = texture(iChannel0, xy);
    vec4 bwTexColor = texture(iChannel1, xy);
    vec4 green = vec4(0.0, 1.0, 0.0, 0.0);
    if(vec4Equal(britTexColor, green)){
        fragColor = bwTexColor;
    }else{
        fragColor = britTexColor;
    }
}
