void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if (mod(iTime, 5.0) <= 1.0)
    {
		fragColor = texture(iChannel1, fragCoord / iResolution.xy);
    }
    else
    {
        fragColor = load_value(mod(fragCoord.xy + vec2(10.0, 0.0), iResolution.xy));
    }
}
