float pick(float v)
{
    if(v > 0.5){
    	return (v - 0.5) * 2.0;
    }
	return v * 2.0;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = vec4(0.0);
    if(uv.x <= 0.5 && uv.y <= 0.5)
    {
        color = texture(iChannel0, vec2(pick(uv.x), pick(uv.y)));
        color = vec4(
            color.r * 0.393 + color.g * 0.769 + color.b * 0.189,
            color.r * 0.349 + color.g * 0.686 + color.b * 0.168,
            color.r * 0.272 + color.g * 0.534 + color.b * 0.131,
            1.0);
    }

    else if(uv.x <= 0.5 && uv.y > 0.5)
    {
    	color = texture(iChannel0, vec2(pick(uv.x), pick(uv.y)));
    }

    else if(uv.x > 0.5 && uv.y <= 0.5)
    {
    	color = texture(iChannel0, vec2(pick(uv.x), pick(uv.y)));
        color = 1.0 - color;
    }


    else if(uv.x > 0.5 && uv.y > 0.5)
    {
    	color = texture(iChannel0, vec2(pick(uv.x), pick(uv.y)));
        float gray = color.r * 0.6 + color.g * 0.3 + color.b * 0.1;
        color = vec4(gray, gray, gray, 1.0);
    }

	fragColor = color;
}
