void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float shade = 0.0;

    float numHorizontalDivisions = 10.0;
    float numVerticalDivisions = 10.0;

    float verticalDivisionSize = 1.0 / numVerticalDivisions;
    float horizontalDivisionSize = 1.0 / numHorizontalDivisions;

    shade += 0.5*floor(20.0*(mod( uv.x, horizontalDivisionSize )));
    shade += 0.5*floor(20.0*(mod( uv.y, verticalDivisionSize )));

    if( shade == 1.0 )
    {
    	shade = 0.0;
    }

    fragColor = vec4( shade, shade, shade, 1.0 );
}
