float path(float x, float t)
{
	return cos(x*2.) + cos(x + sin(x * 3.) * 5.) * 0.2 + sin(x * 5.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float n = 10. + 30. * iMouse.x / iResolution.x;
   	float s = 0.1 + 0.3 * iMouse.y / iResolution.y;

    if (iMouse.x == 0.) n = 10.;
    if (iMouse.y == 0.) s = 0.3;

    float t = iTime * 2.;

    vec2 v = (fragCoord.xy*2. - iResolution.xy) / iResolution.y;
    v.x *= 0.5;

    //v.x += t * 0.1;

    vec2 mv = v;

    float offsetX = t * 0.5;
    float offsetY = 0.5;

    v.x += offsetX;
    v.y += path(v.x,t) * 0.3 + offsetY;
    float line = 1e-4 / v.y / v.y;

	mv.y += path(floor(mv.x*n)/n+offsetX,t) * 0.3 + offsetY;
	mv.x = mod(mv.x, 1.0/n)-0.5/n;

	float rect = length(max(abs(mv) - vec2(0.4/n,s),0.0));
	rect = smoothstep(0.01,0.0,rect);

	fragColor = vec4(rect - line);
}
