float PI = 3.14159265359;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //uv -= vec2(.5);
    float lenght = length(uv*2.);
    float angle = atan(uv.x,uv.y)*20.;
    vec4 circle = vec4(1);
    circle.r = step(fract(lenght*900.),.9+.1*sin(angle));
    circle.g = step(fract(lenght*100.),.9+.1*cos(angle));
    circle.b = texture(iChannel0,vec2(uv.x,uv.y)).b;
    circle = circle*step(length(uv-.5),.5);

    //uv += vec2(.5);

    vec4 britney = texture(iChannel0,vec2(uv.x,uv.y))*circle;
    britney.r = step(britney.r*sin(britney.r*70.),0.);
    britney.g = step(britney.g*cos(britney.g*70.),.0);
	fragColor = britney + texture(iChannel0,vec2(uv.x,uv.y));
    //fragColor = circle;
}
