void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 p = uv*2.-1.;
    p.y /= iResolution.x/iResolution.y;


    vec2 m = (iMouse.xy/iResolution.xy*2.-1.)*vec2(1.0, iResolution.y/iResolution.x);

    if(length(iMouse.xy) < 10.0)
        m = vec2(cos(iTime), 0.);
    p -= m;

    vec3 col = vec3(0.);

    float count = 0.0;
    float rad = (length(p)*10.)+1.;

    float rad2 = rad*rad;
    for(float i=-rad; i<rad; i+=1.)
    for(float j=-rad; j<rad; j+=1.)
    {
        float l2 = i*i+j*j;
        if(l2<rad2)
        {
            float w = 1.0 - sqrt(l2/rad2);
            //w = 1.0;
    		col += w*texture(iChannel0, uv+vec2(i,j)/ iResolution.xy).rgb;
        	count += w;
        }
    }

    col /= count;

    //col = mix(col, vec3(dot(col, vec3(1.)/3.)), 1.-1./sqrt(rad));

	fragColor = vec4(col,1.0);
	//fragColor = vec4(vec3(1./(1.+rad)),1.0);
}
