#define rnd(U) texture(iChannel0, U)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    O -= O; 
    U = ( U+U - R ) / R.y;
	float t = 0.*iTime,
          a = atan(U.y,U.x) + .1*t, r = length(U);           // to polar
    vec4 N;
  //N = rnd( vec2(.4*r,a/6.283)); r+=.0*N.x; a+=.3*N.y; 
  //N = rnd( .2*U); r+=.1*N.x; a+=.3*N.y; 
    r += 1.2*sin(3.*r*6.28)/18.8 * mix( 1., sin(7.*a) , 1.); // base pattern of sliced stem-leaves
    N = rnd( vec2(.4*r,a/6.283)); r+=.04*N.x; a+=.6*N.y;     // perturbations (which space is best ?)
  //N = rnd( .3*U); r+=.04*N.x; a+=.6*N.y; 
  //O = N; return;
    O += .5+.5*sin( 100.*r + 2.*a + 2.*t) ;                  // double spiral
    O *=  vec4(.6,.3,1,1) * 3.2;                             // coloring scheme
}
