void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime/4.0),1.0)
      * sin(iTime/8.0)
      - cos(fragCoord.x * iTime / 17.3 + iTime / 8.0)
      - sin(fragCoord.y * iTime / 11.0 + iTime / 7.0)
      + sin(fragCoord.x / 2910.0 * fragCoord.y / 2230.0
        / ((sin(iTime/419.0
          + sin(fragCoord.x*0.2 + fragCoord.y*0.14))+1.0)/2.0/8.0
        )
        + iTime/78.0 * sin(iTime/200.1)
          * (1.0 + sin(iTime/473.0))*153.7
      ) * sin(fragCoord.x / 3.0 + cos(iTime/4.0)*5.0)
      + cos(fragCoord.y / 31.0
        + sin(iTime/8.0+sin(iTime/40.0))
        + sin(iTime*6.2+fragCoord.x/(
          (1.0+sin(iTime/16.0))*50.0
        ))
        + sin(iTime*4.3+fragCoord.y/(
          (1.0+sin(iTime/11.0))*23.0
        ))
      )
    ;
}
