//Quincy Buchanan
float WidthFrequency = 15.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	//colors
	vec4 white = vec4(1.0,1.0,1.0,1.0);
	vec4 black = vec4(0.0,0.0,0.0,1.0);

    //get the aspect ratio
    float aspectRatio = iResolution.y / iResolution.x;

    //assign horizontal and vertical floors
    float x = floor(uv.x * WidthFrequency);
    float y = floor(uv.y * WidthFrequency * aspectRatio);

    float total = x + y;

    //assign color to every other square
    if(mod(total, 2.0) == 0.0)
    {
        fragColor = white;
    }
    else
    {
        fragColor = black;
    }
}
