vec2 rotate( vec2 pt, float a ){
	mat2 r = mat2(cos(a), -sin(a),
				  sin(a), cos(a) );
	return r * pt;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float z = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 2.0;
    uv -= 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv *= length(uv * 2.0) * 2.0;
    uv = rotate(uv, sin(z * 0.5 + length(uv) * 0.2));


    vec3 c;


    for(int i=0; i<3; i++){
    	vec2 r = iResolution.xy;
    	float v = length(mod(uv,1.0)-0.5);
    	uv.x *= ((cos(z + v) + 1.0) * 0.5) * ((sin(z * 0.5) + 2.0) * 0.25);
    	uv.y *= ((sin(z + v) + 1.0) * 0.5) * ((sin(z * -0.5) + 2.0) * 0.25);
    	c[i] = cos( pow(length(uv * 1.0), 0.5 + float(i) * 0.1 ) );
    }

    vec3 color = c * c;

	fragColor = vec4(color, 1.0);
}
