//Dianna Cornett's checkerboard pattern, version with textures for extra credit

//editable variables for checkerboard pattern
float columns = 7.0;
float rows = 3.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

 vec4 tex1 = texture(iChannel0, uv);
 vec4 tex2 = texture(iChannel1, uv); 

    //loops to set up alternating blocks of checkerboard pattern
    if(int(uv.x*columns)%2 == 0)
    {

        if(int(uv.y*rows)%2 ==0)
        {
            fragColor = tex1;
        }
        else
        {
             fragColor = tex2;
        }

    }
    else
    {

        fragColor = tex1;
        if(int(uv.y*rows)%2 ==0)
        {
            fragColor = tex2;
        }
    }
}
