void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iCoord = ivec2(fragCoord);
    iCoord.x -= 1;
    float fShade = texelFetch(iChannel0, iCoord, 0).r;

    if ( iCoord.x < 0 )
    {
        float v = fragCoord.y / iResolution.y;
        fShade = step(v, iTimeDelta * 15.0);
    }

    fragColor = vec4(fShade);
}
