vec3 bgColor = vec3(0.5, 0.9, 1.0);
vec3 red = vec3(1.0, 0.0,0.0);
vec3 green = vec3(0.0, 1.0, 0.0);
vec3 blue = vec3(0.0, 0.0, 1.0);

vec4 layer1,layer2, layer3, layer4;
vec4 compositeLayers;

const float Layer1Alpha = 1.0; //set transparency
const float Layer2Alpha = 1.0;
const float Layer3Alpha = 1.0;
const float Layer4Alpha = 1.0;

float rect, rectWidth, rectHeight;

vec2 uv;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	uv = fragCoord.xy / iResolution.xy; //normalize
    uv.y *= iResolution.y /iResolution.x; //aspect ratio correction

    layer1 = vec4(bgColor, Layer1Alpha);

    //House
    rectWidth = step(0.25, uv.x) * step(uv.x, 0.8);
    rectHeight = step(0.05, uv.y) * step(uv.y, 0.3);
    rect = rectWidth * rectHeight * Layer2Alpha;

    layer2 = vec4(red, rect);

    //chimney
    rectWidth = step(0.27, uv.x) * step(uv.x, 0.32);
    rectHeight = step(0.3, uv.y) * step(uv.y, 0.4);
    rect = rectWidth * rectHeight * Layer3Alpha;

    layer3 = vec4(blue, rect);

    //windows 
    rectWidth = step(0.47, uv.x) * step(uv.x, 0.57);
    rectHeight = step(0.15, uv.y) * step(uv.y, 0.25);
    rect = rectWidth * rectHeight * Layer4Alpha;

    rectWidth = step(0.3, uv.x) * step(uv.x, 0.4);
    rectHeight = step(0.15, uv.y) * step(uv.y, 0.25);
    rect += rectWidth * rectHeight * Layer4Alpha;

    rectWidth = step(0.64, uv.x) * step(uv.x, 0.74);
    rectHeight = step(0.15, uv.y) * step(uv.y, 0.25);
    rect += rectWidth * rectHeight * Layer4Alpha;

    layer4 = vec4(green, rect);

    //output
    compositeLayers = mix(layer1, layer2, layer2.a);
    compositeLayers = mix(compositeLayers, layer3, layer3.a);
    compositeLayers = mix(compositeLayers, layer4, layer4.a);

	fragColor = compositeLayers;
}
