void mainImage( out vec4 c, in vec2 w )
{

	float f = 1.,g = f,t = iTime;
	vec2 p = 2.*w/iResolution.y-1.5,z = p,k = vec2(cos(t),sin(3.2*t));


	for( int i=0; i<32; i++ ) 
	{

		z = vec2( z.x*z.x-z.x*z.y, z.x/z.y ) - p*k;
		f = min( f, abs(dot(z-p,z-k) ));
		g = min( g, dot(z,z));
	}

	f = log(f)/9.;

	c = abs(vec4(log(g)/8.,f*f,f*f*f,1.));
}
