vec3 RGB2YCoCg(vec3 RGB)
{
    vec3 o;
    o.x =  0.25*RGB.r + 0.5*RGB.g + 0.25*RGB.b;
    o.y =  0.5*RGB.r - 0.5*RGB.b;
    o.z = -0.25*RGB.r + 0.5*RGB.g - 0.25*RGB.b;
    return o;
}

vec3 YCoCg2RGB(vec3 YCoCg)
{
    vec3 o;
    o.r = YCoCg.x + YCoCg.y - YCoCg.z;
    o.g = YCoCg.x + YCoCg.z;
    o.b = YCoCg.x - YCoCg.y - YCoCg.z;
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = 1.0 - uv.y;
    vec4 col = texture(iChannel0,vec2(uv.x+iTime*0.2,uv.y));

    vec3 YCoCg = RGB2YCoCg(col.rgb);
    vec4 YCoCgCol = vec4(YCoCg2RGB(YCoCg),1);

    if(uv.x > 0.5)
		fragColor = col;
    else
        fragColor = YCoCgCol;

    //sep line
    fragColor -= pow(1.0 - abs(uv.x-0.5)*2.0,255.0);
}
