/* based on http://madebyevan.com/shaders/grid/ */

float grid(float width, vec2 step, vec2 pos){
    vec2 coord = pos / step;

    vec2 grid = abs(fract(coord - 0.5) - 0.5) / fwidth(coord * width);
    float line = min(grid.x, grid.y);

    return 1. - min(line, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float v = grid(1., vec2(10.), fragCoord) + grid(2., vec2(100.), fragCoord);

    float s = 500.; // step
    float mp = mod(fragCoord.x - iTime * 100., s);
    float sm = 1. - smoothstep(0., 50., mp) * (1. - smoothstep(s - 450.,s,mp));

    vec3 c = v * vec3(0., 1., 1.) * sm;

    vec3 col = vec3(0.);
    if (iMouse.w > .5) col = vec3(1.);

    c = mix(col, c, v);
	fragColor = vec4(c,1.0);
}
