void mainImage( out vec4 f, in vec2 w ) {
    vec4 p = vec4(w,0.,1.)/iResolution.xyxy-.5, d=p, t;
    float s = sign(d.y);
    p.z -= iTime*4.;
    d.y = -abs(d.y);
    for(float i = 1.5; i >0.; i-=.01)
    {
        t = max(vec4(.3,.7,1,0)*-s, texture(iChannel0, p.xz * .001 * s ));
        f = t*i;
        t.x -= p.y*.04;
        if(t.x>.99) break;
        p += (d-d*t.x)*8.;
    }
}
