void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor *= 0.;
    for(float i=0.; i<32.; i++) {
        vec2 p = fragCoord.xy/iResolution.x-.3, m=vec2(.1,.05);

		p += (fract(sin(iTime + i + p*(14.,1e2) + p.yx*(3., 57.))*1e5)-.5) * (sin(iTime)*.1+dot(p,p))*.1;

		float r = length(p), R = pow(r,.1), f = .1+mod(floor((R+.025)/.05), 3.)/9.;
		p = mod(vec2(atan(p.y, p.x)/ 6.283 + sin(iTime*f), R), m)/m;
		fragColor += (r*1.5)* texture( iChannel0, p-.5)*(1.+sin((R+1.)*120.));
    }
    fragColor /= 32.;
}
