//Classic moving grid pattern
//@thekushsharma

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy * 2.0 / iResolution.x ;   

    uv.y = uv.y + iTime/3.5;        

	vec4 outColor = texture(iChannel0, uv);

    vec4 col = vec4(1.0, 1.0, 1.0, 1.0);
    col.x = cos(iTime)*2.0;
    col.y = (sin(iTime/2.0)*0.5 + 0.5)*2.0;    
    col.z = (sin(iTime)*0.5 + 0.5)*2.0;

    //Multiply color by texture
	fragColor = outColor * col;
}
