#define MAX_ITER 350
#define TIME_SCALE 0.5

float count(float zr, float zc){
    float r = sin(iTime/2.57634*TIME_SCALE),c=sin(iTime/5.782341*TIME_SCALE);
	for(int i = 0; i < MAX_ITER; i++){
        if(zr*zr+zc*zc >= 2.0)
            return float(i);
        float tzr = zr * zr - zc*zc + r;
        zc = zc * zr * 2.0 + c;
        zr = tzr;
	}
    return -1.0;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float color = count((uv[0]*3.0-1.5)/(iResolution.xy[1]/iResolution.xy[0]),(uv[1]*3.0-1.5));
    color = color/float(MAX_ITER);
    if(color < 0.0)
        fragColor = vec4(0,0,0,1);
    else
    	fragColor = texture(iChannel0,vec2(color,sin(iTime/7.87/100.0)));
}
