void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = fragCoord.xy;
    pos.x = pos.x / iResolution.x;
    pos.y = pos.y / iResolution.y;

    vec4 colorDraw = vec4(0, 0, 0, 1.0);
    colorDraw.r = pos.y * 2.0 *  abs(sin(iTime));
    colorDraw.g = pos.y / pos.x * pos.y - 2.0 * abs(cos(iTime - 2.0));
    colorDraw.b = pos.x / pos.y * pos.x + 0.8 * abs(cos(iTime - 2.0));

    fragColor = colorDraw;
}
