vec2 rotate (inout vec2 uv, float t)
{
	float rad = radians(360.0* t);
	mat2 rotatez = mat2(cos(rad),sin(rad),-sin(rad),cos(rad));
	return rotatez * (uv - 0.5) + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //vec2[] uvs = vec2[] (uv);
    int NbLoops = 10;
    float offset = 0.005*sin(iDate.w*10.);

    vec4 img = vec4(0.0); 

    for (int i = 0; i< NbLoops; i++)
    {
        img += texture(iChannel0, rotate(uv, float(i)*offset));
        //uvs[i] = rotate(uv, float(i)*offset);
    }    

    float bloom = 1.3;

    img = vec4(pow(img.r, bloom), pow(img.g, bloom), pow(img.b, bloom), 1.);

	fragColor = vec4(img/float(NbLoops));
}
