void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.y/iResolution.x;
    vec2 uv = fragCoord.xy / iResolution.x - vec2(0.5,ratio/2.0);
    vec2 P = 75.0 * uv;
    float TI = mod(5.0 * iTime,6.2831855);
    float ST = sin(iTime);
    float L = length(P);
    float A = (atan(P.x,P.y) + L*ST*ST + TI);
    float C = 1.5 * sin(A * 5.0);
    fragColor = vec4(-C, C*ST*ST*ST, C, 1.0);
}
